mapper.select:
SELECT *
FROM genFile
WHERE 1
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}
AND typeCode = #{typeCode}
AND type2Code = #{type2Code}
AND fileNo = #{fileNo}

mapper.update:
UPDATE genFile

	updateDate = NOW(),
	relId = #{relId}

WHERE id = #{id}

mapper.select:
SELECT *
FROM genFile
WHERE 1
AND relTypeCode = #{relTypeCode}
AND relId = #{relId}

	AND typeCode = #{typeCode}


	AND type2Code = #{type2Code}

ORDER BY typeCode, type2Code, fileNo

mapper.delete:
DELETE FROM genFile
WHERE relTypeCode = #{relTypeCode}
AND relId = #{relId}
